
public class ForTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<5;i++) { //5회전되는 반복문
			//i=0, i=1, i=2, i=3, i=4
			System.out.println("안녕!!!");//5번 실행
		}
		
		int j = 0;
		
		while(j < 5) { //5회전되는 반복문
			//j=0, j=1, j=2, j=3, j=4
			System.out.println("안녕!!!");//5번 실행
			j++;//j=j+1
		}
		
	}

}
